\begin{tikzpicture}[scale=1.5]
  % Define angles and lengths
  \pgfmathsetmacro{\i}{30} % incidence angle
  \pgfmathsetmacro{\r}{45} % refraction angle
  \pgfmathsetmacro{\d}{1}  % boundary spacing
  \pgfmathsetmacro{\len}{2} % wavefront segment length
  \pgfmathsetmacro{\cosi}{cos(\i)}
  \pgfmathsetmacro{\sini}{sin(\i)}
  \pgfmathsetmacro{\cosr}{cos(\r)}
  \pgfmathsetmacro{\sinr}{sin(\r)}
  \pgfmathsetmacro{\arcr}{0.3} % arc radius

  % Draw boundary
  \draw[dashed] (-2.5,0) -- (2.5,0) node[right] {Boundary};

  % Draw normal
  \draw[dashed] (0,-1.5) -- (0,1.5) node[above] {Normal};

  % Draw incident wavefronts (shallow water)
  \foreach \k in {-1,0,1} {
    \draw[blue, thick] ({\k*\d},0) -- ({\k*\d + \len*\cosi}, {0 - \len*\sini});
  }

  % Draw refracted wavefronts (deep water)
  \foreach \k in {-1,0,1} {
    \draw[red, thick] ({\k*\d},0) -- ({\k*\d - \len*\cosr}, {0 + \len*\sinr});
  }

  % Draw incident ray
  \draw[->, >=stealth, thick, green!50!black] 
    ({-1.5*sin(\i)}, {-1.5*cos(\i)}) -- (0,0);

  % Draw refracted ray
  \draw[->, >=stealth, thick, green!50!black] 
    (0,0) -- ({1.5*sin(\r)}, {1.5*cos(\r)});

  % Draw incident angle arc
  \draw (0,0) -- (0,-\arcr);
  \draw (0,-\arcr) arc (-90:-90+\i:\arcr) 
    node[midway, anchor=north east] {$i$};

  % Draw refracted angle arc
  \draw (0,0) -- (0,\arcr);
  \draw (0,\arcr) arc (90:90-\r:\arcr) 
    node[midway, anchor=south east] {$r$};

  % Region labels
  \node[anchor=east] at (-2,-1) {Shallow water};
  \node[anchor=east] at (-2,1) {Deep water};
\end{tikzpicture}
